Remove-Item .\build -Recurse; Remove-Item .\build.7z; New-Item -ItemType Directory -Path .\build; hugo -t main --destination ./build/main; hugo -t simple --destination ./build/simple; 7z a build.7z .\build; ssh root@spiketail "rm -rfv /root/build; rm build.7z;"; scp build.7z root@spiketail:/root/; ssh root@spiketail "7z x build.7z; rm -rfv /var/www/realja.me/*; rm -rfv /var/www/simple/*; cp -rv /root/build/main/* /var/www/realja.me;cp -rv /root/build/simple /var/www/; chmod -R 755 /var/www/realja.me; chmod -R 755 /var/www/simple; chown -R james:www-data /var/www/realja.me; chown -R james:www-data /var/www/simple;"